import fileType from '@marklb/file-type'
// import { Buffer } from 'buffer/'
const Buffer = require('buffer/').Buffer

export function readFileAsync(file: any): Promise<ArrayBuffer | null> {
  return new Promise<ArrayBuffer | null>((resolve, reject) => {
    const reader = new FileReader()
    reader.onload = () => {
      resolve(reader.result as ArrayBuffer | null)
    }
    reader.readAsArrayBuffer(file)
  })
}

export async function readFileAsDataUrlAsync(file: Blob): Promise<string | null> {
  return new Promise<string | null>((resolve, reject) => {
    const reader = new FileReader()
    reader.onload = () => {
      resolve(reader.result as string | null)
    }
    reader.readAsDataURL(file)
  })
}

export async function fileBufferToBlob(
  fileBuffer: Buffer,
  defaultMime: string = 'application/octet-stream'
): Promise<Blob> {
  const fType = fileType(fileBuffer)
  const mime = (fType) ? fType.mime : defaultMime
  const blob = new Blob([fileBuffer as any], { type: mime })
  return blob
}

export async function fileBufferToObjectUrl(
  fileBuffer: Buffer,
  defaultMime: string = 'application/octet-stream'
): Promise<string> {
  const file = await fileBufferToBlob(fileBuffer, defaultMime)
  const fileURL = URL.createObjectURL(file)
  return fileURL
}

export interface IFileData {
  ext?: string
  mime?: string
  blob: Blob
}

export async function fileDataFromBuffer(
  fileBuffer: Buffer | Uint8Array | ArrayBuffer,
  defaultMime: string = 'application/octet-stream'
): Promise<IFileData> {
  const _fileBuffer = Buffer.from(fileBuffer)
  const fType = fileType(_fileBuffer as any)
  const ext = fType && fType.ext || undefined
  const mime = fType ? fType.mime : defaultMime
  const blob = new Blob([_fileBuffer as any], { type: mime })
  const fileData: IFileData = { ext, mime, blob }
  return fileData
}

export function openBlob(blob: Blob, target?: string, filename?: string) {
  // NOTE: IE and MSEdge do not allow Blob resources as a source for
  // tabs or iframes. msSaveOrOpenBlob is used as a workaround. I
  // haven't been able to find a way to just open the Blob file in
  // another tab yet for IE or MSEdge.
  if (window.navigator.msSaveOrOpenBlob) {
    window.navigator.msSaveOrOpenBlob(blob, filename)
  } else {
    const url = URL.createObjectURL(blob)
    const win = window.open(url, target)
    // TODO: Consider if always setting opener to null is to restrictive
    // if (win && target && target.toLowerCase() === '_blank') {
    //   win.opener = null
    // }

  }
}

import { ComponentType } from '@angular/cdk/portal'
import { ChangeDetectionStrategy, Component, Inject, Input, OnInit } from '@angular/core'
import { BehaviorSubject, combineLatest, from, Observable, of } from 'rxjs'
import { concatMap, map, switchMap, take, tap, toArray } from 'rxjs/operators'

import jexl from 'jexl'

import { IDataExporter, THESEAM_DATA_EXPORTER } from '../data-exporter/index'
import { THESEAM_DATA_FILTER_DEF } from '../data-filters/data-filter-def'
import { IDataFilter } from '../data-filters/index'
import { notNullOrUndefined } from '../utils/index'

import { IDatatableDynamicDef, IDynamicDatatableRow, IDynamicDatatableRowActionDef } from './datatable-dynamic-def'

export function jexlObservable<R = any>(expression: string, context?: any): Observable<R> {
  return from(jexl.eval(expression, context) as Promise<R>)
}

export interface IFilterComponentRecord {
  component: ComponentType<IDataFilter>
  options?: any
  order?: number
}

export interface IActionRowExprContext {
  row: IDynamicDatatableRow
}

@Component({
  selector: 'seam-datatable-dynamic',
  templateUrl: './datatable-dynamic.component.html',
  styleUrls: ['./datatable-dynamic.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class DatatableDynamicComponent implements OnInit {

  @Input()
  set data(value: IDatatableDynamicDef | undefined | null) {
    if (value) {
      this._setDefaults(value)
    }
    this._data.next(value)
  }
  get data() { return this._data.value }
  private _data = new BehaviorSubject<IDatatableDynamicDef | undefined | null>(undefined)

  public data$ = this._data.asObservable()

  _exporters$: Observable<IDataExporter[] | undefined>
  _commonFilterComponents$: Observable<IFilterComponentRecord[]>
  _hasFullSearch$: Observable<boolean>
  _hasFilterMenu$: Observable<boolean>

  constructor(
    @Inject(THESEAM_DATA_EXPORTER) public _dataExporters: IDataExporter[],
    @Inject(THESEAM_DATA_FILTER_DEF) public _dataFilters: { name: string, component: ComponentType<IDataFilter> }[]
  ) { }

  ngOnInit() {
    this._exporters$ = this.data$.pipe(map(data => {
      if (data && data.filterMenu && Array.isArray(data.filterMenu.exporters)) {
        return data.filterMenu.exporters
          .map(e => this._dataExporters.find(de => de.name === e))
          .filter(notNullOrUndefined)
      }
      return undefined
    }))

    this._commonFilterComponents$ = this.data$.pipe(map(data => {
      if (
        data && data.filterMenu && Array.isArray(data.filterMenu.filters) &&
        this._dataFilters && Array.isArray(this._dataFilters)
      ) {
        const commonFilters = data.filterMenu.filters.filter(f => f.type === 'common')

        if (!commonFilters || commonFilters.length < 1) {
          return []
        }

        const r = commonFilters
          .map(cf => {
            const _df = this._dataFilters.find(df => df.name === cf.name)
            if (_df) {
              const record: IFilterComponentRecord = {
                component: _df.component,
                options: cf.options,
                order: cf.order || 0
              }
              return record
            }
            return null
          })
          .filter(notNullOrUndefined)

        return r
      }
      return []
    }))

    this._hasFullSearch$ = this.data$.pipe(map(data => {
      if (data && data.filterMenu && Array.isArray(data.filterMenu.filters)
        && data.filterMenu.filters.findIndex(f => f.type === 'full-search') !== -1
      ) {
        return true
      }
      return false
    }))

    this._hasFilterMenu$ = this.data$.pipe(
      switchMap(data => {
        if (data && data.filterMenu) {
          if (data.filterMenu.state === 'always-visible') {
            return of(true)
          } else if (data.filterMenu.state === 'hidden') {
            return of(false)
          }
        }

        return combineLatest([
          this._exporters$.pipe(map(e => (e || []).length > 0)),
          this._commonFilterComponents$.pipe(map(cfc => cfc.length > 0)),
          this._hasFullSearch$
        ]).pipe(map(v => v.indexOf(true) !== -1))
      })
    )
  }

  private _setDefaults(def: IDatatableDynamicDef): void {
    for (const col of def.columns) {
      if (!col.cellType) {
        col.cellType = 'string'
      }
    }

    if (def.filterMenu) {
      if (!def.filterMenu.state) {
        def.filterMenu.state = 'default'
      }
    }

    if (def.options) {
      if (def.options.virtualization === undefined || def.options.virtualization === null) {
        def.options.virtualization = false
      }
    }
  }

  public _rowActions(row: IDynamicDatatableRow, rowActions: IDynamicDatatableRowActionDef[]): Observable<IDynamicDatatableRowActionDef[]> {
    // TODO: Fix async eval.

    if (!rowActions) { return of([]) }

    return from(rowActions).pipe(
      // concatMap(rowAction => rowAction.isHiddenExpr
      //   ? jexlObservable(rowAction.isHiddenExpr).pipe(take(1), map(v => v ? rowAction : undefined))
      //   : of(rowAction)
      // ),

      map(rowAction => {
        if (rowAction.isHiddenExpr) {
          const context = this._getActionRowContext(row, rowAction)
          const isHidden = jexl.evalSync(rowAction.isHiddenExpr, context)
          return isHidden ? undefined : rowAction
        }
        return rowAction
      }),

      toArray(),
      map(v => v.filter(notNullOrUndefined)),
      // tap(r => console.log('result', r)),
    )
  }

  private _getActionRowContext(row: IDynamicDatatableRow, rowActionDef: IDynamicDatatableRowActionDef): IActionRowExprContext {
    return {
      row
    }
  }

}

import { Directive, ElementRef, HostListener, Input } from '@angular/core'

declare type _PointerEvent = PointerEvent | PointerEvent

export interface IHoverClassToggleRecord {
  default: string
  hover: string
}

/**
 * Don't use this directive unless there is a reason not to use css `:hover`,
 * because the accuracy of detecting the hover is not as good. If an element is
 * moved while the mouse is idle the hover class will remain until the mouse
 * enters and leaves again.
 *
 * I have tried multiple ways of detecting the mouse no longer hovering, but all
 * have introduced a slight performance issue that just builds for each element
 * containing this directive.
 */
@Directive({
  selector: '[seamHoverClassToggle]'
})
export class HoverClassToggleDirective {

  private _hovered = false
  private _defaultClasses: string[] = []
  private _hoverClasses: string[] = []

  @HostListener('mouseover', ['$event']) onMouseOver($event: MouseEvent) {
    this._setHovered(true)
  }

  @HostListener('mouseout', ['$event']) onMouseOut($event: MouseEvent) {
    this._setHovered(false)
  }

  @HostListener('pointerover', ['$event']) onPointerOver($event: _PointerEvent) {
    this._setHovered(true)
  }

  @HostListener('pointerout', ['$event']) onPointerOut($event: _PointerEvent) {
    this._setHovered(false)
  }

  @Input()
  set seamHoverClassToggle(value: IHoverClassToggleRecord) {
    if (value) {
      if (value.default) {
        this._defaultClasses = value.default.split(' ').filter(c => c.length > 0)
      }
      if (value.hover) {
        this._hoverClasses = value.hover.split(' ').filter(c => c.length > 0)
      }
    }

    this._hoverClasses = this._hoverClasses.filter(v => !this._defaultClasses.find(_v => _v === v))

    this._update()
  }

  constructor(
    private _element: ElementRef
  ) { }

  private _update() {
    for (const c of this._defaultClasses) {
      this._hovered ?  this._removeClass(c) : this._addClass(c)
    }
    for (const c of this._hoverClasses) {
      this._hovered ? this._addClass(c) : this._removeClass(c)
    }
  }

  private _setHovered(hovered) {
    const doUpdate = this._hovered !== hovered
    this._hovered = hovered
    if (doUpdate) {
      this._update()
    }
  }

  private _addClass(c: string) {
    this._element.nativeElement.classList.add(c)
  }

  public _removeClass(c: string) {
    this._element.nativeElement.classList.remove(c)
  }

}

