import { ComponentPortal } from '@angular/cdk/portal'
import { Injectable } from '@angular/core'
import { BehaviorSubject, Observable } from 'rxjs'
import { map, mapTo, take, tap } from 'rxjs/operators'

import { notNullOrUndefined } from '../../../utils/index'

import { IDashboardWidgetsColumnRecord, IDashboardWidgetsItem, IDashboardWidgetsItemDef } from './dashboard-widgets-item'

@Injectable({
  providedIn: 'root'
})
export class DashboardWidgetsService {

  get widgets(): IDashboardWidgetsItemDef[] { return this._widgets.value }
  set widgets(value: IDashboardWidgetsItemDef[]) { this._widgets.next(value) }
  private _widgets = new BehaviorSubject<IDashboardWidgetsItemDef[]>([])

  public widgetItems$: Observable<IDashboardWidgetsItem[]>
  public widgetColumns$: Observable<IDashboardWidgetsColumnRecord[]>

  /** Used for operations, such as 'addWidget', if the column is not specified. */
  public defaultColumn = 0

  constructor() {
    this.widgetItems$ = this._widgets
      .pipe(
        map(defs => defs.map(d => this.createWidgetItem(d)).filter(notNullOrUndefined))
      )

    this.widgetColumns$ = this.widgetItems$
      .pipe(
        // Distribute items into columns
        map(items => {
          const columns: IDashboardWidgetsColumnRecord[] = []

          for (const item of items) {
            const col: IDashboardWidgetsColumnRecord | undefined = columns.find(c => c.column === item.col)
            if (!col) {
              columns.push({ column: item.col, items: [ item ] })
            } else {
              col.items.push(item)
            }
          }

          return columns
        }),
        // Sort columns
        map(columns => columns.sort((a, b) => a.column - b.column)),
        // Sort columns items
        tap(columns => columns.forEach(col => col.items.sort((a, b) => a.order - b.order)))
      )
  }

  public createWidgetItem(def: IDashboardWidgetsItemDef): IDashboardWidgetsItem {
    const item: IDashboardWidgetsItem = {
      ...def,
      col: def.col || this.defaultColumn,
      order: def.order || 0,
      portal: this.createWidgetPortal(def),
      __itemDef: def
    }

    return item
  }

  public createWidgetPortal(def: IDashboardWidgetsItemDef): ComponentPortal<any> {
    let portal: ComponentPortal<any>
    if (def.componentFactoryResolver) {
      portal = new ComponentPortal(def.type, undefined, undefined, def.componentFactoryResolver)
    } else {
      portal = new ComponentPortal(def.type)
    }
    return portal
  }

  public updateOrder(): Observable<void> {
    return this.widgetColumns$
      .pipe(
        take(1),
        tap(columns => columns.forEach(col => {
          let i = 0
          col.items.forEach(itm => itm.order = i++)
        })),
        mapTo(undefined)
      )
  }

}

import { Injectable } from '@angular/core'
import { BehaviorSubject, fromEvent, Observable } from 'rxjs'
import { filter, map, tap } from 'rxjs/operators'

import * as localStorageMemory from 'localstorage-memory'

import { notNullOrUndefined } from '../utils/index'

// NOTE: Temporary localStorage polyfill just to get the app running without localStorage for now.
const localStorage: Storage = 'localStorage' in window && window.localStorage != null ? window.localStorage : localStorageMemory

export interface ILocalStorageService {
  select(key: string, defaultValue: string | null): Observable<string | null>
  get(key: string, defaultValue: string | null): string | null
  set(key: string, value: string): void
  remove(key: string): void
}

@Injectable({
  providedIn: 'root'
})
export class LocalStorageService implements ILocalStorageService {
  protected subjects: { [key: string]: BehaviorSubject<string | null> } = {}

  /** This is only here for testing/debugging. */
  private _localStorage = localStorage

  constructor() {
    fromEvent<StorageEvent>(window, 'storage')
      .pipe(
        map(e => e.key),
        filter(notNullOrUndefined),
        tap(key => {
          const subjectValue = this.get(key)
          const storedValue = this._localStorage.getItem(key)
          if (subjectValue !== storedValue) {
            if (storedValue) {
              this.set(key, storedValue)
            } else {
              this.remove(key)
            }
          }
        }),
      )
      .subscribe()
  }

  /**
   * Observe a localStorage item.
   *
   * NOTE: Only emits changes if the item is changed with the set method of this
   * class instance.
   */
  select(key: string, defaultValue: string | null = null): Observable<string | null> {
    if (this.subjects.hasOwnProperty(key)) {
      return this.subjects[key]
    }

    if (!this._localStorage.getItem(key) && defaultValue) {
      this._localStorage.setItem(key, defaultValue)
    }

    const value = this._localStorage.getItem(key) || defaultValue

    return (this.subjects[key] = new BehaviorSubject(value))
  }

  /** Get a localStorage item. */
  get(key: string, defaultValue: string | null = null): string | null {
    if (this.subjects.hasOwnProperty(key)) {
      return this.subjects[key].value
    }

    if (!this._localStorage.getItem(key) && defaultValue) {
      this._localStorage.setItem(key, defaultValue)
    }

    const value = this._localStorage.getItem(key) || defaultValue

    return (this.subjects[key] = new BehaviorSubject(value)).value
  }

  /** Set a localStorage item. */
  set(key: string, value: string): void {
    this._localStorage.setItem(key, value)

    if (this.subjects.hasOwnProperty(key)) {
      this.subjects[key].next(value)
    }
  }

  /** Remove a localStorage item. */
  remove(key: string): void {
    this._localStorage.removeItem(key)

    if (this.subjects.hasOwnProperty(key)) {
      this.subjects[key].next(null)
    }
  }
}

import { Directionality } from '@angular/cdk/bidi'
import {
  ComponentType,
  Overlay,
  OverlayConfig,
  OverlayRef,
  ScrollStrategy,
} from '@angular/cdk/overlay'
import { ComponentPortal, PortalInjector, TemplatePortal } from '@angular/cdk/portal'
import { Location } from '@angular/common'
import {
  ComponentFactoryResolver,
  ComponentRef,
  Inject,
  Injectable,
  Injector,
  OnDestroy,
  Optional,
  SkipSelf,
  TemplateRef,
  Type
} from '@angular/core'
import { defer, Observable, of, of as observableOf, Subject } from 'rxjs'
import { startWith, switchMap } from 'rxjs/operators'

import { TheSeamDynamicComponentLoader } from '../dynamic-component-loader/index'
import { OverlayScrollbarsService } from '../scrollbar/index'

import { ModalConfig } from './modal-config'
import { ModalContainerComponent } from './modal-container/modal-container.component'
import {
  MODAL_CONFIG,
  MODAL_CONTAINER,
  MODAL_DATA,
  MODAL_REF,
  MODAL_SCROLL_STRATEGY,
} from './modal-injectors'
import { ModalRef } from './modal-ref'


/**
 * Service to open modal dialogs.
 */
@Injectable()
export class Modal implements OnDestroy {
  private _scrollStrategy: () => ScrollStrategy

  /** Stream that emits when all dialogs are closed. */
  get _afterAllClosed(): Observable<void> {
    return this._parentDialog ? this._parentDialog.afterAllClosed : this._afterAllClosedBase
  }
  _afterAllClosedBase = new Subject<void>()

  afterAllClosed: Observable<void> = defer(() => this.openDialogs.length ?
      this._afterAllClosed : this._afterAllClosed.pipe(startWith(undefined)))

  /** Stream that emits when a dialog is opened. */
  get afterOpened(): Subject<ModalRef<any>> {
    return this._parentDialog ? this._parentDialog.afterOpened : this._afterOpened
  }
  _afterOpened: Subject<ModalRef<any>> = new Subject()

  /** Stream that emits when a dialog is opened. */
  get openDialogs(): ModalRef<any>[] {
    return this._parentDialog ? this._parentDialog.openDialogs : this._openDialogs
  }
  _openDialogs: ModalRef<any>[] = []

  constructor(
    private overlay: Overlay,
    private injector: Injector,
    @Inject(MODAL_REF) private dialogRefConstructor: Type<ModalRef<any>>,
    @Inject(MODAL_SCROLL_STRATEGY) scrollStrategy: any,
    @Optional() @SkipSelf() private _parentDialog: Modal,
    @Optional() location: Location,
    private _scrollbars: OverlayScrollbarsService,
    private _dynamicComponentLoaderModule: TheSeamDynamicComponentLoader
  ) {

    // Close all of the dialogs when the user goes forwards/backwards in history or when the
    // location hash changes. Note that this usually doesn't include clicking on links (unless
    // the user is using the `HashLocationStrategy`).
    if (!_parentDialog && location) {
      location.subscribe(() => this.closeAll())
    }

    this._scrollStrategy = scrollStrategy
  }

  /** Gets an open dialog by id. */
  getById(id: string): ModalRef<any> | undefined {
    return this._openDialogs.find(ref  => ref.id === id)
  }

  /** Closes all open dialogs. */
  closeAll(): void {
    this.openDialogs.forEach(ref => ref.close())
  }

  /** Opens a dialog from a component. */
  openFromComponent<T, D = any>(
    component: ComponentType<T>,
    config?: ModalConfig<D>,
    // NOTE: Should only be needed with the current implementation of `TheSeamDynamicComponentLoader`.
    componentFactoryResolver?: ComponentFactoryResolver | null | undefined
  ): ModalRef<T, D> {
    config = this._applyConfigDefaults(config)

    if (config.id && this.getById(config.id)) {
      throw Error(`Modal with id "${config.id}" exists already. The modal id must be unique.`)
    }

    const overlayRef = this._createOverlay(config)
    const dialogContainer = this._attachDialogContainer(overlayRef, config)
    const dialogRef = this._attachDialogContentForComponent(component, dialogContainer,
      overlayRef, config, componentFactoryResolver)

    this._scrollbars.initializeInstance(overlayRef.overlayElement)

    this.registerDialogRef(dialogRef)
    return dialogRef
  }

  /** Opens a dialog from a template. */
  openFromTemplate<T>(template: TemplateRef<T>, config?: ModalConfig): ModalRef<any> {
    config = this._applyConfigDefaults(config)

    if (config.id && this.getById(config.id)) {
      throw Error(`Modal with id "${config.id}" exists already. The modal id must be unique.`)
    }

    const overlayRef = this._createOverlay(config)
    const dialogContainer = this._attachDialogContainer(overlayRef, config)
    const dialogRef = this._attachDialogContentForTemplate(template, dialogContainer,
      overlayRef, config)

    this._scrollbars.destroyInstance(overlayRef.overlayElement)

    this.registerDialogRef(dialogRef)
    return dialogRef
  }

  /** Opens a dialog from a lazy-loaded component. */
  openFromLazyComponent<T, D = any>(
    componentId: string,
    config?: ModalConfig<D>
  ): Observable<ModalRef<T, D>> {
    config = this._applyConfigDefaults(config)

    if (config.id && this.getById(config.id)) {
      throw Error(`Modal with id "${config.id}" exists already. The modal id must be unique.`)
    }

    return this._dynamicComponentLoaderModule
      .getComponentFactory<{}>(componentId)
      .pipe(
        switchMap(componentFactory => {
          const modalRef = this.openFromComponent(
            componentFactory.componentType,
            config,
            (<any /* ComponentFactoryBoundToModule */>componentFactory).ngModule.componentFactoryResolver
          )
          return of(modalRef)
        })
      )
  }

  ngOnDestroy() {
    // Only close all the dialogs at this level.
    this._openDialogs.forEach(ref => ref.close())
  }

  /**
   * Forwards emitting events for when dialogs are opened and all dialogs are closed.
   */
  private registerDialogRef(dialogRef: ModalRef<any>): void {
    this.openDialogs.push(dialogRef)

    const dialogOpenSub = dialogRef.afterOpened().subscribe(() => {
      this.afterOpened.next(dialogRef)
      dialogOpenSub.unsubscribe()
    })

    const dialogCloseSub = dialogRef.afterClosed().subscribe(() => {
      const dialogIndex = this._openDialogs.indexOf(dialogRef)

      if (dialogIndex > -1) {
        this._openDialogs.splice(dialogIndex, 1)
      }

      if (!this._openDialogs.length) {
        this._afterAllClosedBase.next()
        dialogCloseSub.unsubscribe()
      }
    })
  }

  /**
   * Creates an overlay config from a dialog config.
   * @param config The dialog configuration.
   * @returns The overlay configuration.
   */
  protected _createOverlay(config: ModalConfig): OverlayRef {
    let panelClass = (config.panelClass || [])
    if (typeof panelClass === 'string') {
      panelClass = [ panelClass ]
    }
    panelClass = [ ...panelClass, 'modal',  'd-block', 'overflow-auto' ]

    const overlayConfig = new OverlayConfig({
      positionStrategy: this.overlay.position().global(),
      scrollStrategy: this._scrollStrategy(),
      panelClass: panelClass,
      hasBackdrop: config.hasBackdrop,
      direction: config.direction,
      minWidth: config.minWidth,
      minHeight: config.minHeight,
      maxWidth: config.maxWidth,
      maxHeight: config.maxHeight
    })

    if (config.backdropClass) {
      overlayConfig.backdropClass = config.backdropClass
    }
    return this.overlay.create(overlayConfig)
  }

  /**
   * Attaches an MatDialogContainer to a dialog's already-created overlay.
   * @param overlay Reference to the dialog's underlying overlay.
   * @param config The dialog configuration.
   * @returns A promise resolving to a ComponentRef for the attached container.
   */
  protected _attachDialogContainer(overlay: OverlayRef, config: ModalConfig): ModalContainerComponent {
    const container = config.containerComponent || this.injector.get(MODAL_CONTAINER)
    const userInjector = config && config.viewContainerRef && config.viewContainerRef.injector
    const injector = new PortalInjector(userInjector || this.injector, new WeakMap([
      [ModalConfig, config]
    ]))
    const containerPortal = new ComponentPortal(container, config.viewContainerRef, injector)
    const containerRef = <ComponentRef<ModalContainerComponent>>overlay.attach(containerPortal)
    containerRef.instance._config = config

    return containerRef.instance
  }


  /**
   * Attaches the user-provided component to the already-created MatDialogContainer.
   * @param componentOrTemplateRef The type of component being loaded into the dialog,
   *     or a TemplateRef to instantiate as the content.
   * @param dialogContainer Reference to the wrapping MatDialogContainer.
   * @param overlayRef Reference to the overlay in which the dialog resides.
   * @param config The dialog configuration.
   * @returns A promise resolving to the MatDialogRef that should be returned to the user.
   */
  protected _attachDialogContentForComponent<T>(
      componentOrTemplateRef: ComponentType<T>,
      dialogContainer: ModalContainerComponent,
      overlayRef: OverlayRef,
      config: ModalConfig,
      componentFactoryResolver?: ComponentFactoryResolver | null | undefined): ModalRef<any> {

    // Create a reference to the dialog we're creating in order to give the user a handle
    // to modify and close it.
    const dialogRef = new this.dialogRefConstructor(overlayRef, dialogContainer, config.id)
    const injector = this._createInjector<T>(config, dialogRef, dialogContainer)
    const contentRef = dialogContainer.attachComponentPortal(
        new ComponentPortal(componentOrTemplateRef, undefined, injector, componentFactoryResolver))

    dialogRef.componentInstance = contentRef.instance
    dialogRef.disableClose = config.disableClose

    dialogRef.updateSize({width: config.width, height: config.height})
             .updatePosition(config.position)

    return dialogRef
  }

  /**
   * Attaches the user-provided component to the already-created MatDialogContainer.
   * @param componentOrTemplateRef The type of component being loaded into the dialog,
   *     or a TemplateRef to instantiate as the content.
   * @param dialogContainer Reference to the wrapping MatDialogContainer.
   * @param overlayRef Reference to the overlay in which the dialog resides.
   * @param config The dialog configuration.
   * @returns A promise resolving to the MatDialogRef that should be returned to the user.
   */
  protected _attachDialogContentForTemplate<T>(
      componentOrTemplateRef: TemplateRef<T>,
      dialogContainer: ModalContainerComponent,
      overlayRef: OverlayRef,
      config: ModalConfig): ModalRef<any> {

    // Create a reference to the dialog we're creating in order to give the user a handle
    // to modify and close it.
    const dialogRef = new this.dialogRefConstructor(overlayRef, dialogContainer, config.id)

    dialogContainer.attachTemplatePortal(
      new TemplatePortal<T>(componentOrTemplateRef, <any>null,
        <any>{$implicit: config.data, dialogRef}))
    dialogRef.updateSize({width: config.width, height: config.height})
             .updatePosition(config.position)

    return dialogRef
  }


  /**
   * Creates a custom injector to be used inside the dialog. This allows a component loaded inside
   * of a dialog to close itself and, optionally, to return a value.
   * @param config Config object that is used to construct the dialog.
   * @param dialogRef Reference to the dialog.
   * @param container Dialog container element that wraps all of the contents.
   * @returns The custom injector that can be used inside the dialog.
   */
  private _createInjector<T>(
      config: ModalConfig,
      dialogRef: ModalRef<T>,
      dialogContainer: ModalContainerComponent): PortalInjector {

    const userInjector = config && config.viewContainerRef && config.viewContainerRef.injector
    const injectionTokens = new WeakMap<any, any>([
      [this.injector.get(MODAL_REF), dialogRef],
      [this.injector.get(MODAL_CONTAINER), dialogContainer],
      [MODAL_DATA, config.data]
    ])

    if (config.direction &&
        (!userInjector || !userInjector.get<Directionality | null>(Directionality, null))) {
      injectionTokens.set(Directionality, {
        value: config.direction,
        change: observableOf()
      })
    }

    return new PortalInjector(userInjector || this.injector, injectionTokens)
  }

  /**
   * Expands the provided configuration object to include the default values for properties which
   * are undefined.
   */
  private _applyConfigDefaults(config?: ModalConfig): ModalConfig {
    const dialogConfig = (<unknown>this.injector.get(MODAL_CONFIG)) as typeof ModalConfig
    return {...new dialogConfig(), ...config}
  }
}

import { Platform } from '@angular/cdk/platform'
import { forwardRef, inject, Injectable, InjectionToken, Injector, INJECTOR, NgZone } from '@angular/core'
import { fromEvent, Subscription } from 'rxjs'

import OverlayScrollbars from 'overlayscrollbars'

import { LIB_OVERLAY_SCROLLBARS_CONFIG } from './overlay-scrollbars-config'
import { IOverlayScrollbarsConfig } from './overlay-scrollbars-config-model'

@Injectable({
  providedIn: 'root'
})
export class OverlayScrollbarsService {

  private _inputEventSubscription = Subscription.EMPTY

  constructor(
    private _ngZone: NgZone,
    private injector: Injector,
    private _platform: Platform
  ) { }

  public initializeInstance(element: HTMLElement, options?: IOverlayScrollbarsConfig): void {
    if (!this.isInstanceEnabled(element) &&
      // The 'overlayscrollbars' library is causing an issue on iOS. Since iOS
      // already has native overlay scrollbars it shouldn't really effect the
      // app layout.
      !(this._isTextarea(element) && this._platform.IOS)
    ) {
      this._ngZone.runOutsideAngular(() => {
        OverlayScrollbars(element, this._applyConfigDefaults(options))

        if (this._isTextarea(element)) {
          // TODO: Make this more accurate. Right now this is just relying on
          // the fact that timing out for 100ms is usually enough time to wait
          // for update to correctly calculate.
          // NOTE: This may be fixed in a newer version to not need this hack.
          fromEvent(element, 'change').subscribe(_ => {
            this._ngZone.run(() => {
              setTimeout(() => {
                if (this.isInstanceEnabled(element)) {
                  this.getInstance(element).update()
                }
              }, 100)
            })
          })
        }
      })
    }
  }

  public destroyInstance(element: HTMLElement): void {
    if (this.isInstanceEnabled(element)) {
      this._ngZone.runOutsideAngular(() => {
        this.getInstance(element).destroy()
        if (this._inputEventSubscription && !this._inputEventSubscription.closed) {
          this._inputEventSubscription.unsubscribe()
        }
      })
    }
  }

  public getInstance(element: HTMLElement): OverlayScrollbars {
    return OverlayScrollbars(element) as OverlayScrollbars
  }

  public isInstanceEnabled(element: HTMLElement): boolean {
    return !!this.getInstance(element)
  }

  public setOptions(element: HTMLElement, options: IOverlayScrollbarsConfig): boolean {
    if (!this.isInstanceEnabled(element)) { return false }

    this.getInstance(element).options(this._applyConfigDefaults(options))

    return true
  }

  public getOptions(element: HTMLElement): IOverlayScrollbarsConfig {
    return this.getInstance(element).options()
  }

  private _applyConfigDefaults(config?: IOverlayScrollbarsConfig): IOverlayScrollbarsConfig {
    const _config: IOverlayScrollbarsConfig = this.injector.get(LIB_OVERLAY_SCROLLBARS_CONFIG)
    return {..._config, ...config}
  }

  /** Determines if the component host is a textarea. */
  protected _isTextarea(element: HTMLElement) {
    return element.nodeName.toLowerCase() === 'textarea'
  }

}

import { Injectable } from '@angular/core'
import { NavigationEnd, Router, UrlTree } from '@angular/router'
import { Observable } from 'rxjs'
import { filter, map, startWith } from 'rxjs/operators'

@Injectable({
  providedIn: 'root'
})
export class RouterHelpersService {

  constructor(
    private _router: Router
  ) { }

  public isActive(url: string | UrlTree, exact: boolean): Observable<boolean> {
    return this._router.events.pipe(
      filter(event => event instanceof NavigationEnd),
      map(event => this._router.isActive(url, exact)),
      startWith(this._router.isActive(url, exact))
    )
  }
}

import { Injectable } from '@angular/core'

import { Modal, ModalRef } from '../modal/index'
import { ThemeTypes } from '../models/index'

import { ConfirmDialogComponent } from './confirm-dialog.component'

@Injectable()
export class SeamConfirmDialogService {

  constructor(
    private modal: Modal
  ) { }

  public open(
    message?: string,
    alert?: string | { message: string, type: ThemeTypes }
  ): ModalRef<ConfirmDialogComponent, 'confirm' | undefined> {
    const modalRef = this.modal.openFromComponent(ConfirmDialogComponent)

    if (!modalRef.componentInstance) {
      throw new Error('ConfirmDialogComponent not created.')
    }

    const comp: ConfirmDialogComponent = modalRef.componentInstance

    if (message) {
      comp.message = message
    }

    if (alert) {
      if (typeof alert === 'string') {
        comp.alertMessage = alert
      } else if (alert.message && alert.type) {
        comp.alertMessage = alert.message
        comp.alertType = alert.type
      } else {
        throw new Error('Invalid alert argument.')
      }
    }

    return modalRef
  }

}

import { Injectable, InjectionToken, Injector } from '@angular/core'
import { NavigationEnd, Router } from '@angular/router'
import { filter, take } from 'rxjs/operators'

export const STORY_INITIAL_ROUTE_URL = new InjectionToken<any>('STORY_INITIAL_ROUTE_URL')

export function storyInitialRouteFactory(_storyInitialRouteService: StoryInitialRouteService) {
  return () => _storyInitialRouteService.setInitialRoute()
}

@Injectable()
export class StoryInitialRouteService {

  constructor(
    private _injector: Injector
  ) { }

  public setInitialRoute() {
    const _router = this._injector.get(Router)
    const url = this._injector.get(STORY_INITIAL_ROUTE_URL)
    if (_router.navigated) {
      _router.navigateByUrl(url)
    } else {
      _router.events.pipe(
          filter(e => e instanceof NavigationEnd),
          take(1)
        )
        .subscribe(() => { _router.navigateByUrl(url) })
    }
  }

}

