import { Injectable } from '@angular/core'

import { TabbedComponent } from './tabbed.component'

export declare type TabsDirection = 'horizontal' | 'vertical'

@Injectable()
export class TabbedService {

  private _tabGroups = {}

  constructor() { }

  public registerTab(tab: TabbedComponent, groupName: string) {
    if (!this._tabGroups[groupName]) {
      this._tabGroups[groupName] = []
    }

    for (const t of this._tabGroups[groupName]) {
      t.hideTabs = true
    }

    this._tabGroups[groupName].push(tab)
  }

  public unregisterTab(tab: TabbedComponent, groupName: string) {
    if (this._tabGroups[groupName]) {
      this._tabGroups[groupName] = this._tabGroups[groupName].filter(
        t => t !== tab
      )

      if (this._tabGroups[groupName].length > 0) {
        this._tabGroups[groupName][this._tabGroups[groupName].length - 1].hideTabs = false
      }
    }
  }

}

import { Injectable } from '@angular/core'

import jexl from 'jexl'

import { DynamicDatatableCellActionModal } from '../../datatable-dynamic/index'
import { TheSeamDynamicComponentLoader } from '../../dynamic-component-loader/index'
import { Modal, ModalConfig, ModalRef } from '../../modal/index'
import { ITableCellData } from '../../table/table-cell.models'

import { Observable, of, Subject, Subscriber } from 'rxjs'
import { switchMap, takeUntil } from 'rxjs/operators'
import { CaluclatedValueContextType, ICalucatedValueContext } from '../table-cell-types-models'

@Injectable({
  providedIn: 'root'
})
export class TableCellTypesHelpersService {

  constructor(
    private _dynamicComponentLoaderModule: TheSeamDynamicComponentLoader,
    private _modal: Modal,
  ) { }

  public parseValueProp(value: any, contextOrContextFn: CaluclatedValueContextType) {
    if (value === undefined || value === null) {
      return value
    }

    if (typeof value === 'string') {
      return value
    }

    if (value.type === 'jexl') {
      const context = this._resolveValueContext(contextOrContextFn)
      return jexl.evalSync(value.expr, context)
    }

    return value
  }

  public getValueContext(value: any, data?: ITableCellData<any, string>): ICalucatedValueContext {
    return {
      row: data && data.row,
      rowIndex: data && data.rowIndex,
      colData: data && data.colData,
      value: value
    }
  }

  private _resolveValueContext(contextOrContextFn: CaluclatedValueContextType) {
    let context = contextOrContextFn
    if (typeof context === 'function') {
      context = context()
    }
    return context
  }

  public handleModalAction<R = any>(
    action: DynamicDatatableCellActionModal,
    contextOrContextFn: CaluclatedValueContextType
  ) {
    // TODO: Try to simplify this observable. It seems fairly easy to read like
    // this, but seems like it is doing more than it needs to with the multiple
    // subjects.
    return new Observable((subscriber: Subscriber<R>) => {
      // Passed to helper function to emit results to subscriber.
      const resultSubject = new Subject<any>()
      // Used to stop observing.
      const stopSubject = new Subject<any>()

      // Observe results to emit to subscriber.
      resultSubject
        .pipe(takeUntil(stopSubject))
        .subscribe(
          result => subscriber.next(result),
          err => subscriber.error(err),
          () => stopSubject.complete()
        )

      // Handle the potentially deep chain of actions.
      this._handleModalAction(action, contextOrContextFn, resultSubject)
        .pipe(takeUntil(stopSubject))
        .subscribe(
          () => {},
          err => subscriber.error(err),
          () => subscriber.complete()
        )

      // Clean up on unsubscribe.
      return () => {
        stopSubject.next(undefined)
        stopSubject.complete()
      }
    })
  }

  private _handleModalAction(
    action: DynamicDatatableCellActionModal,
    contextOrContextFn: CaluclatedValueContextType,
    resultSubject: Subject<any>
  ) {
    const data = this.parseValueProp(action.data, contextOrContextFn)

    return this._handleModalActionOpenModal(action, data).pipe(
      switchMap(modalRef => modalRef.afterClosed().pipe(
        switchMap(result => {
          resultSubject.next(result)

          const resultAction = this._getModalResultAction(action, result)
          if (resultAction) {
            return this._handleModalAction(resultAction, contextOrContextFn, resultSubject)
          }

          resultSubject.complete()
          return of(undefined)
        })
      ))
    )
  }

  private _handleModalActionOpenModal(action: DynamicDatatableCellActionModal, data?: any): Observable<ModalRef<any, any>> {
    if (!action.component) {
      throw new Error('Cell action type "modal" must have a component defined.')
    }

    const config: ModalConfig<any> = {
      modalSize: 'lg',
      data
    }

    if (typeof action.component === 'string') {
      return this._dynamicComponentLoaderModule
        .getComponentFactory<{}>(action.component)
        .pipe(
          switchMap(componentFactory => {
            const modalRef = this._modal.openFromComponent(
              componentFactory.componentType,
              config,
              (<any /* ComponentFactoryBoundToModule */>componentFactory).ngModule.componentFactoryResolver
            )
            return of(modalRef)
          })
        )
    } else {
      const modalRef = this._modal.openFromComponent(action.component, config)
      return of(modalRef)
    }
  }

  private _getModalResultAction(action: DynamicDatatableCellActionModal, result: any) {
    if (
      action.resultActions
      && action.resultActions[result]
      && action.resultActions[result].type === 'modal'
    ) {
      return action.resultActions[result]
    }
  }
}

import { Injectable } from '@angular/core'
import { ActivatedRoute, NavigationEnd, Router } from '@angular/router'
import { combineLatest, Observable, of } from 'rxjs'
import { filter, map, startWith, switchMap } from 'rxjs/operators'

import { activatedRoutesWithDataProperty, IActivatedRouteWithData, routeSnapshotPathFull } from '../utils/index'

import { ITheSeamBreadcrumb } from './breadcrumb'

@Injectable({
  providedIn: 'root'
})
export class TheSeamBreadcrumbsService {

  public readonly breadcrumbDataKey = 'breadcrumb'

  public crumbs$: Observable<ITheSeamBreadcrumb[]>

  constructor(
    private _router: Router,
    private _activatedRoute: ActivatedRoute
  ) {
    this.crumbs$ = this._router.events.pipe(
      filter(event => event instanceof NavigationEnd),
      map(_ => this._activatedRoute),
      startWith(this._activatedRoute),
      activatedRoutesWithDataProperty(this.breadcrumbDataKey, true),
      switchMap(rwdArr => combineLatest(rwdArr.map(rwd => this._parseBreadcrumbData(rwd))))
    )
  }

  private _parseBreadcrumbData(routeWithData: IActivatedRouteWithData): Observable<ITheSeamBreadcrumb> {
    const crumbValue = routeWithData.data[this.breadcrumbDataKey]
    const route = routeWithData.route
    const path = routeSnapshotPathFull(route.snapshot)
    let value = ''

    if (typeof crumbValue === 'string') {
      value = crumbValue
    } else {
      console.warn(
        '[TheSeamBreadcrumbsService] Only string breadcrumbs are supported currently. '
        + 'Use a resolver if the value needs to be dynamically calculated.'
      )
    }

    return of({ value, path, route })
  }

}

import { Injectable, NgZone } from '@angular/core'

import { OverlayScrollbarsService } from '../../scrollbar/index'

@Injectable()
export class TheSeamDatatableScrollbarHelperService {

  width = 0

  /**
   * Animates programmatic scrolling, such as switching pages.
   *
   * NOTE: Animating the scrolling works, but the datatable may need changes
   * before this is enabled, because it seems a little stuttery when it moves.
   */
  animatedScrolling = false
  animatedScrollingTime = 250

  constructor(
    private _ngZone: NgZone,
    private _scrollbars: OverlayScrollbarsService
  ) { }

  onInitScroller(scroller: any): void {
    this._scrollbars.initializeInstance(scroller.parentElement, {
      callbacks: {
        onScroll: (e) => {
          this._ngZone.run(() => {
            scroller.onScrolled(e)
          })
        }
      }
    })
  }

  onDestroyScroller(scroller: any): void {
    this._scrollbars.destroyInstance(scroller.parentElement)
  }

  setOffset(scroller: any, offsetY: number): void {
    // TODO: Move the NgZone decision to the service
    this._ngZone.runOutsideAngular(() => {
      if (this.animatedScrolling) {
        this._scrollbars.getInstance(scroller.parentElement)
          .scroll({ y: offsetY }, this.animatedScrollingTime)
      } else {
        this._scrollbars.getInstance(scroller.parentElement)
          .scroll({ y: offsetY })
      }
    })
  }
}

import { Compiler, ComponentFactory, Inject, Injectable, Injector, NgModuleFactory, NgModuleFactoryLoader, Optional } from '@angular/core'
import { Observable, of } from 'rxjs'
import { from, throwError } from 'rxjs'
import { mergeMap, switchMap } from 'rxjs/operators'

import { LoadChildren } from '@angular/router'
import { IDynamicComponentManifest } from './dynamic-component-manifest'
import {
  DYNAMIC_COMPONENT,
  DYNAMIC_COMPONENT_MANIFESTS,
  DYNAMIC_MODULE
} from './dynamic-component-tokens'

import { wrapIntoObservable } from '../utils/index'

@Injectable({
  providedIn: 'root'
})
export class TheSeamDynamicComponentLoader {

  private _manifests: IDynamicComponentManifest[]

  constructor(
    private compiler: Compiler,
    private injector: Injector,
    @Optional() @Inject(DYNAMIC_COMPONENT_MANIFESTS) manifests: IDynamicComponentManifest[],
    // TODO: Remove the loader now that all our code should have updated from
    // the string `loadChildren` by now.
    @Optional() private _loader: NgModuleFactoryLoader
  ) { this._manifests = manifests || [] }

  /** Retrieve a ComponentFactory, based on the specified componentId (defined in the IDynamicComponentManifest array). */
  getComponentFactory<T>(componentId: string, injector?: Injector): Observable<ComponentFactory<T>> {
    const manifest = this._manifests
      .find(m => m.componentId === componentId)
    if (!manifest) {
      return throwError(`TheSeamDynamicComponentLoader: Unknown componentId "${componentId}"`)
    }

    // const path = manifest.loadChildren

    // const p = this.load<T>(path, componentId, injector)
    // return from(p)

    const moduleFactory$ = this.loadModuleFactory(manifest.loadChildren)

    return moduleFactory$
      .pipe(switchMap(m => from(this.loadFactory<any>(m, componentId, injector))))
  }

  load<T>(path: string, componentId: string, injector?: Injector): Promise<ComponentFactory<T>> {
    return this._loader.load(path)
      .then((ngModuleFactory) => this.loadFactory<T>(ngModuleFactory, componentId, injector))
  }

  private loadModuleFactory(loadChildren: LoadChildren): Observable<NgModuleFactory<any>> {
    if (typeof loadChildren === 'string') {
      return from(this._loader.load(loadChildren))
    } else {
      return wrapIntoObservable(loadChildren()).pipe(mergeMap((t: any) => {
        if (t instanceof NgModuleFactory) {
          return of(t)
        } else {
          return from(this.compiler.compileModuleAsync(t))
        }
      }))
    }
  }

  private loadFactory<T>(ngModuleFactory: NgModuleFactory<any>, componentId: string, injector?: Injector): Promise<ComponentFactory<T>> {
    const moduleRef = ngModuleFactory.create(injector || this.injector)
    const dynamicComponentType = moduleRef.injector.get(DYNAMIC_COMPONENT, null)
    if (!dynamicComponentType) {
      const dynamicModule: IDynamicComponentManifest = moduleRef.injector.get(DYNAMIC_MODULE, null)

      if (!dynamicModule) {
        throw new Error(
          'TheSeamDynamicComponentLoader: Dynamic module for'
          + ` componentId "${componentId}" does not contain`
          + ' DYNAMIC_COMPONENT or DYNAMIC_MODULE as a provider.',
        )
      }
      if (dynamicModule.componentId !== componentId) {
        throw new Error(
          'TheSeamDynamicComponentLoader: Dynamic module for'
          + `${componentId} does not match manifest.`,
        )
      }

      const path = dynamicModule.loadChildren

      if (!path) {
        throw new Error(`${componentId} unknown!`)
      }

      // return this.load<T>(path, componentId, injector)
      const moduleFactory$ = this.loadModuleFactory(dynamicModule.loadChildren)

      return moduleFactory$
        .pipe(switchMap(m => from(this.loadFactory<any>(m, componentId, injector))))
        .toPromise()
    }

    return Promise.resolve(moduleRef.componentFactoryResolver.resolveComponentFactory<T>(dynamicComponentType))
  }
}

