import { Overlay, OverlayRef } from '@angular/cdk/overlay'
import { ComponentPortal } from '@angular/cdk/portal'
import { Injectable } from '@angular/core'
import { Observable, throwError } from 'rxjs'
import { catchError, finalize, tap } from 'rxjs/operators'

import { LoadingComponent } from './loading/loading.component'

@Injectable()
export class TheSeamLoadingOverlayService {

  private _enabled = false

  private _overlayRef: OverlayRef

  constructor(
    private _overlay: Overlay
  ) { }

  get enabled(): boolean {
    return this._enabled
  }

  public toggle(enabled?: boolean) {
    const enable = enabled === undefined ? !this.enabled : enabled
    if (enable && !this.enabled) {
      this.enable()
    } else if (!enable && this.enabled) {
      this.disable()
    }
  }

  public enable() {
    if (this.enabled) { return }

    this._overlayRef = this._overlay.create({
      hasBackdrop: false,
      width: '100%',
      height: '100%'
    })
    this._overlayRef.attach(new ComponentPortal(LoadingComponent))

    this._enabled = true
  }

  public disable() {
    if (!this.enabled) { return }

    this._overlayRef.dispose()

    this._enabled = false
  }

  public while<T = any>(source: Observable<T>): Observable<T> {
    this.enable()
    return source
      .pipe(
        tap(() => this.disable()),
        catchError(err => {
          this.disable()
          return throwError(err)
        }),
        finalize(() => this.disable())
      )
  }

}

import { DOCUMENT } from '@angular/common'
import { Inject, Injectable } from '@angular/core'

import { ICanToggleEdit } from './models'

@Injectable({
  providedIn: 'root'
})
export class ToggleEditKeyboardListenerService {

  private _elements: ICanToggleEdit[] = []

  private _isListening = false

  private _document: Document

  constructor(
    @Inject(DOCUMENT) private document: any
  ) {
    this._document = document
  }

  public add(element: ICanToggleEdit): void {
    this._elements.push(element)
    this._startListening()
  }

  public remove(element: ICanToggleEdit): void {
    this._elements = this._elements.filter(v => v !== element)
    if (this._elements.length === 0) {
      this._stopListening()
    }
  }

  public isListening(): boolean {
    return this._isListening
  }

  private _startListening(): void {
    if (this.isListening()) {
      return
    }

    this._document.body.addEventListener('keydown', this._keydownListener, true)

    this._isListening = true
  }

  private _stopListening(): void {
    if (!this.isListening()) {
      return
    }

    this._document.body.removeEventListener('keydown', this._keydownListener, true)

    this._isListening = false
  }

  private _keydownListener = (event: KeyboardEvent): boolean | void => {
    for (const element of this._elements) {
      if (element.isEditing()) {
        element.keydownEvent(event)
      }
    }
  }

}

import { ComponentPortal } from '@angular/cdk/portal'
import { ComponentFactoryResolver, Inject, Injectable, Injector, ViewContainerRef } from '@angular/core'
import { Observable, of, throwError } from 'rxjs'
import { map } from 'rxjs/operators'

import { TheSeamDynamicComponentLoader } from '../dynamic-component-loader/dynamic-component-loader.service'

import { IWidgetRegistryRecord } from './widget-registry.models'
import { THESEAM_WIDGETS } from './widget-token'

/**
 * Registry is to provide a way to manage widgets in a way that we can load them
 * the same way throughout the app, whether the component itself is registered,
 * the component is lazy-loaded, or referenced by an id stored in the database.
 */
@Injectable({
  providedIn: 'root'
})
export class WidgetRegistryService {

  constructor(
    @Inject(THESEAM_WIDGETS) private _widgets: IWidgetRegistryRecord[],
    private _dynamicComponentLoader: TheSeamDynamicComponentLoader
  ) { }

  public createWidgetPortal<T>(
    widgetId: string,
    viewContainerRef?: ViewContainerRef | null,
    injector?: Injector | null,
    componentFactoryResolver?: ComponentFactoryResolver | null | undefined
  ): Observable<ComponentPortal<T>> {
    const widgetDef = (this._widgets || []).find(w => w.widgetId === widgetId)

    if (!widgetDef) {
      return throwError(`WidgetRegstry: Unknown widgetId "${widgetId}"`)
    }

    if (typeof widgetDef.componentOrComponentId === 'string') {
      return this._dynamicComponentLoader
        .getComponentFactory<T>(widgetDef.componentOrComponentId)
        .pipe(
          map(componentFactory => {
            let resolver: ComponentFactoryResolver | null | undefined = componentFactoryResolver
            if (!resolver) {
              const m = (<any /* ComponentFactoryBoundToModule */>componentFactory).ngModule
              if (m && m.componentFactoryResolver) {
                resolver = m.componentFactoryResolver
              }
            }

            const portal = new ComponentPortal(
              componentFactory.componentType,
              viewContainerRef,
              injector,
              resolver
            )

            return portal
          }
        )
      )
    } else {
      const portal = new ComponentPortal(
        widgetDef.componentOrComponentId,
        viewContainerRef,
        injector,
        componentFactoryResolver
      )

      return of(portal)
    }
  }
}

