import { Directive, ElementRef, EventEmitter, HostListener, Input, Output } from '@angular/core'

@Directive({
  selector: '[seamClickOutside]'
})
export class ClickOutsideDirective {

  @Input()
  get seamClickOutsideActive(): boolean {
    return this._active
  }
  set seamClickOutsideActive(value: boolean) {
    if (this._active === value) { return }

    if (value) {
      // `setTimeout` is used to avoid the click event triggering when the value
      // changes if the value was changed with a click.
      setTimeout(() => { this._active = value })
    } else {
      this._active = value
    }
  }
  private _active = true

  @Output() seamClickOutside = new EventEmitter<MouseEvent>()

  // NOTE: This should probably be changed from a `HostListener` to allow the
  // subscription to be unsubscribed.
  @HostListener('document:click', ['$event'])
  public onDocumentClick(event: MouseEvent): void {
    if (this._active) {
      const targetElement = event.target as HTMLElement
      // Check if the click was outside the element
      if (targetElement && !this.elementRef.nativeElement.contains(targetElement)) {
        this.seamClickOutside.emit(event)
      }
    }
  }

  constructor(private elementRef: ElementRef) { }

}

import { Directive, EventEmitter, HostBinding, HostListener, Input, OnDestroy, Output } from '@angular/core'

import { ThemeTypes } from '../models/index'
import { ModalRef } from './../modal/modal-ref'

import { ConfirmDialogComponent } from './confirm-dialog.component'
import { SeamConfirmDialogService } from './confirm-dialog.service'

@Directive({
  selector: '[seamConfirmClick]',
  exportAs: 'seamConfirmClick'
})
export class ConfirmClickDirective implements OnDestroy {

  private _modalRef: ModalRef<ConfirmDialogComponent, 'confirm' | undefined> | undefined

  @Input() libConfirmMsg: string
  @Input() libConfirmAlert: string | { message: string, type: ThemeTypes }
  @Input() libConfirmDisabled = false

  @Output() seamConfirmClick = new EventEmitter<'confirm'>()

  @HostBinding('class.lib-confirm-click-active')
  get _confirmClickActiveCss() { return !!this._modalRef }

  @HostListener('click', [ '$event' ])
  _onClick(event: any) {
    if (this.libConfirmDisabled) {
      if (!!this._modalRef) {
        this._modalRef.close()
        this._modalRef = undefined
      }
    }

    if (!!this._modalRef) { return }

    this._modalRef = this._confirmService.open(this.libConfirmMsg, this.libConfirmAlert)

    this._modalRef.afterClosed().subscribe(result => {
      if (result === 'confirm') {
        this.seamConfirmClick.emit(result)
      }

      this._modalRef = undefined
    })
  }

  constructor(private _confirmService: SeamConfirmDialogService) { }

  ngOnDestroy() {
    if (this._modalRef) {
      this._modalRef.close()
    }
  }

  get modalRef() { return this._modalRef }

  public close() {
    if (this._modalRef) {
      this._modalRef.close()
    }
  }

}

import { ESCAPE } from '@angular/cdk/keycodes'
import { ConnectionPositionPair, Overlay, OverlayRef, PositionStrategy } from '@angular/cdk/overlay'
import { TemplatePortal } from '@angular/cdk/portal'
import { Directive, ElementRef, HostListener, Input, TemplateRef, ViewContainerRef } from '@angular/core'

@Directive({
  selector: '[seamDatatableActionMenuToggle]',
  exportAs: 'seamDatatableActionMenuToggle'
})
export class DatatableActionMenuToggleDirective {

  @Input() seamDatatableActionMenuToggle: TemplateRef<HTMLElement>

  private _active = false
  private _overlayRef: OverlayRef
  private _actionDown = false

  @HostListener('document:keydown', [ '$event' ])
  _onKeydown(event: any) {
    if (event.keyCode === ESCAPE) {
      this.disable()
    }
  }

  @HostListener('click', [ '$event' ])
  _onClick(event: any) {
    this.toggle()
  }

  @HostListener('mousedown', [ '$event' ]) _mouseDown(event: any) { this.onInputDown(event) }
  @HostListener('pointerdown', [ '$event' ]) _pointerDown(event: any) { this.onInputDown(event) }

  @HostListener('mouseup', [ '$event' ]) _mouseUp(event: any) { this.onInputUp(event) }
  @HostListener('pointerup', [ '$event' ]) _pointerUp(event: any) { this.onInputUp(event) }

  constructor(
    private _elementRef: ElementRef<HTMLElement>,
    private _viewContainerRef: ViewContainerRef,
    private _overlay: Overlay,
  ) { }

  public get active(): boolean { return this._active }

  public toggle() {
    if (this._active) {
      this.disable()
    } else {
      this.enable()
    }
  }

  public enable() {
    if (this.active) { return }

    this._overlayRef = this._overlay.create({
      hasBackdrop: false,
      positionStrategy: this.getOverlayPosition(this._elementRef.nativeElement),
    })

    this._overlayRef.attach(new TemplatePortal(this.seamDatatableActionMenuToggle, this._viewContainerRef))

    this._active = true
  }

  public disable() {
    if (!this.active) { return }

    if (this._overlayRef.hasAttached()) {
      this._overlayRef.detach()
    }

    this._active = false
  }

  private getOverlayPosition(origin: HTMLElement): PositionStrategy {
    const positionStrategy = this._overlay.position()
      .flexibleConnectedTo(origin)
      .withPositions(this.getPositions())
      .withFlexibleDimensions(false)
      .withPush(false)

    return positionStrategy
  }

  private getPositions(): ConnectionPositionPair[] {
    return [
      {
        originX: 'start',
        originY: 'bottom',
        overlayX: 'end',
        overlayY: 'top',
      },
      {
        originX: 'start',
        originY: 'top',
        overlayX: 'end',
        overlayY: 'bottom',
      },
    ]
