  onFocusChangeOverlay(event: any) {
    if (event === null) {
      if (!this._actionDown) {
        this.disable()
      }
    }
  }

  onInputDown(event: any) {
    this._actionDown = true
  }

  onInputUp(event: any) {
    this._actionDown = false
  }

}

