import { coerceArray } from '@angular/cdk/coercion'
import { Component, forwardRef, Inject, Input, OnDestroy, OnInit, Optional } from '@angular/core'
import { FormControl } from '@angular/forms'
import { untilDestroyed } from 'ngx-take-until-destroy'
import { Observable } from 'rxjs'
import { map, startWith } from 'rxjs/operators'

import { DatatableMenuBarComponent } from '../../../datatable/datatable-menu-bar/datatable-menu-bar.component'
import { isNullOrUndefined } from '../../../utils/index'
import { IDataFilter, THESEAM_DATA_FILTER, THESEAM_DATA_FILTER_OPTIONS } from '../../data-filter'

import { textDataFilter } from '../data-filter-text/data-filter-text.component'
import { ITextFilterOptions } from '../data-filter-text/text-filter-options'


export const DATA_FILTER_TOGGLE_BUTTON: any = {
  provide: THESEAM_DATA_FILTER,
  // tslint:disable-next-line:no-use-before-declare
  useExisting: forwardRef(() => DataFilterToggleButtonsComponent),
  multi: true,
}

export interface IToggleButton {
  name: string
  value: string
  comparator: (value, row, index) => -1 | 0 | 1
}

export interface IToggleButtonsFilterOptions extends ITextFilterOptions {
  selectionToggleable: boolean
  multiple: boolean
  buttons: IToggleButton[]
}

export const DefaultToggleButtonsFilterOptions: IToggleButtonsFilterOptions = {
  properties: undefined,
  omitProperties: undefined,
  multiple: false,
  selectionToggleable: false,
  buttons: [],
  exact: false,
  caseSensitive: false
}

export function toggleButtonsFilter(data: any[], values: string[], options = DefaultToggleButtonsFilterOptions) {
  if (!data || !values) {
    return data
  }

  const customComparatorBtns: IToggleButton[] = []
  for (const btn of options.buttons) {
    if (btn.comparator) {
      customComparatorBtns.push(btn)
    }
  }

  let _data = data
  for (const val of values) {
    if (val === '') { continue }

    let cmp
    for (const btn of customComparatorBtns) {
      if (btn.value === val) {
        cmp = btn.comparator
      }
    }
    if (cmp) {
      const filtered: any[] = []
      for (let i = 0; i < _data.length; i++) {
        if (cmp(val, data[i], i) !== -1) {
          filtered.push(data[i])
        }
      }
      _data = filtered
    } else {
      _data = textDataFilter(_data, val, {
        properties: options.properties,
        omitProperties: options.omitProperties,
        exact: options.exact,
        caseSensitive: options.caseSensitive
      })
    }
  }
  return _data
}

let _uid = 0

@Component({
  selector: 'seam-data-filter-toggle-buttons',
  templateUrl: './data-filter-toggle-buttons.component.html',
  styleUrls: ['./data-filter-toggle-buttons.component.scss'],
  providers: [ DATA_FILTER_TOGGLE_BUTTON ]
})
export class DataFilterToggleButtonsComponent implements OnInit, OnDestroy, IDataFilter {

  public readonly name = 'toggle-buttons'
  public readonly uid = `toggle-buttons__${_uid++}`

  _control = new FormControl()

  @Input() properties = this._optDefault('properties')
  @Input() omitProperties = this._optDefault('omitProperties')
  @Input() multiple = this._optDefault('multiple')
  @Input() selectionToggleable = this._optDefault('selectionToggleable')
  @Input() buttons = this._optDefault('buttons')
  @Input() exact = this._optDefault('exact')
  @Input() caseSensitive = this._optDefault('caseSensitive')

  @Input()
  set value(value: string | string[]) {
    const _value = !isNullOrUndefined(value) ? coerceArray(value) : undefined
    if (this._control.value !== _value) {
      this._control.setValue(_value)
    }
  }

  constructor(
    private _menuBar: DatatableMenuBarComponent,
    @Optional() @Inject(THESEAM_DATA_FILTER_OPTIONS) private _filterOptions: IToggleButtonsFilterOptions | null
  ) { }

  ngOnInit() { this._menuBar.addFilter(this) }

  ngOnDestroy() { this._menuBar.removeFilter(this) }

  private _optDefault<K extends keyof IToggleButtonsFilterOptions>(prop: K) {
    if (this._filterOptions && this._filterOptions.hasOwnProperty(prop)) {
      return this._filterOptions[prop]
    }
    return DefaultToggleButtonsFilterOptions[prop]
  }

  get options(): IToggleButtonsFilterOptions {
    return {
      properties: this.properties,
      omitProperties: this.omitProperties,
      multiple: this.multiple,
      selectionToggleable: this.selectionToggleable,
      buttons: this.buttons,
      exact: this.exact,
      caseSensitive: this.caseSensitive
    }
  }

  public filter<T>(data: T[]): Observable<T[]> {
    return this._control.valueChanges
      .pipe(
        untilDestroyed(this),
        map(v => toggleButtonsFilter(data, coerceArray(v), this.options)),
        startWith(toggleButtonsFilter(data, coerceArray(this._control.value), this.options)),
      )
  }

}

import { Component, ContentChild, ContentChildren, Input, QueryList, Renderer2 } from '@angular/core'
import { NavigationExtras, Router } from '@angular/router'

import { SeamConfirmDialogService } from '../../confirm-dialog/index'
import { DatatableActionMenuItemComponent } from '../datatable-action-menu-item/datatable-action-menu-item.component'

@Component({
  selector: 'seam-datatable-action-menu',
  templateUrl: './datatable-action-menu.component.html',
  styleUrls: ['./datatable-action-menu.component.scss']
})
export class DatatableActionMenuComponent {

  @Input() row

  @ContentChildren(DatatableActionMenuItemComponent) items: QueryList<DatatableActionMenuItemComponent>

  constructor(
    private _renderer: Renderer2,
    private _confirmDialog: SeamConfirmDialogService,
    private _router: Router
  ) { }

  activateItem(event: any, item: DatatableActionMenuItemComponent) {
    if (item.confirmDialog) {
      this._confirmDialog.open(item.confirmDialog.message, item.confirmDialog.alert)
        .afterClosed()
        .subscribe(v => {
          if (v === 'confirm') {
            item.click.emit(event)
            if (item.href) {
              const win = window.open(item.href, item.target)
              // TODO: Consider if always setting opener to null is to restrictive
              // if (win && item.target && item.target.toLowerCase() === '_blank') {
              //   win.opener = null
              // }
            } else {
              const extras: NavigationExtras = {}
              if (item.queryParams) { extras.queryParams = item.queryParams }
              if (item.fragment) { extras.fragment = item.fragment }
              if (item.queryParamsHandling) { extras.queryParamsHandling = item.queryParamsHandling }
              if (item.preserveFragment) { extras.preserveFragment = item.preserveFragment }
              if (item.skipLocationChange) { extras.skipLocationChange = item.skipLocationChange }
              if (item.replaceUrl) { extras.replaceUrl = item.replaceUrl }
              if (item.state) { extras.state = item.state }

              const commands = Array.isArray(item.routerLink) ? item.routerLink : [item.routerLink]
              this._router.navigate(commands, extras)
            }
          }
        })
    } else {
      item.click.emit(event)
    }


    // HACK: This is only here until the action events are emitting to a place
    // the toggler can listen for them.
    this._renderer.setAttribute(event.view.document.body, 'tabindex', '-1')
    event.view.document.body.focus()
    this._renderer.removeAttribute(event.view.document.body, 'tabindex')
  }

}

