@Component({
  selector: 'seam-password-input-reveal',
  template: `
    <fa-icon [icon]="passwordVisible ? faEyeSlash : faEye" class="password-reveal-icon" (click)="revealPassword()"></fa-icon>
  `,
  styles: [
    `
    :host {
      display: flex;
      align-items: center;
      position: absolute;
      top: 0;
