import { AfterViewInit, Component, ElementRef, HostBinding, Input, OnDestroy, OnInit, ViewChild } from '@angular/core'
import { untilDestroyed } from 'ngx-take-until-destroy'
import { from, Observable, Subject } from 'rxjs'
import { auditTime, switchMap } from 'rxjs/operators'

import { waitOnConditionAsync } from '../../../utils/index'

const pdfjsLib = require('pdfjs-dist/build/pdf')

@Component({
  selector: 'seam-pdf-page',
  template: `
  <div #pdfContainer
    (seamElemResized)="onResized($event)">
    <canvas #pdfCanvas></canvas>
  </div>
  `,
  styles: [`
    :host { display: block; }
    canvas { display: block; }
  `]
})
export class PdfPageComponent implements OnInit, OnDestroy, AfterViewInit {

  @Input()
  public get page() { return this._page }
  public set page(value) {
    this._page = value
    setTimeout(_ => { this.render() })
  }
  private _page

  @Input() shadow = false

  @HostBinding('class.shadow') get _shadow() { return this.shadow }

  @ViewChild('pdfContainer', { static: true }) pdfContainer: ElementRef<HTMLDivElement>
  @ViewChild('pdfCanvas', { static: true }) pdfCanvas: ElementRef<HTMLCanvasElement>

  /**
   * Canvas will responsively scale and rerender if scaled more than
   * `renderUpdateThreshold` pixels.
   */
  @Input() responsive = false

  /**
   * The canvas will be rerendered if the canvas size changes by this many
   * pixels from the last render.
   *
   * To make the canvas responsive using only the initial size for its render
   * set the threshold to `-1`. You can still trigger a rerender by calling the
   * `render()` method.
   *
   * NOTE: Only used when `responsive` is `true`.
   */
  @Input() renderUpdateThreshold = 100

  public rendering = false

  private _renderRequestSubject = new Subject<void>()

  private _render$: Observable<void>

  constructor() {
    if (!pdfjsLib.GlobalWorkerOptions.workerSrc) {
      // tslint:disable-next-line:max-line-length
      pdfjsLib.GlobalWorkerOptions.workerSrc = `https://cdnjs.cloudflare.com/ajax/libs/pdf.js/${ (pdfjsLib as any).version }/pdf.worker.min.js`
    }
  }

  ngOnInit() {
    this._render$ = this._renderRequestSubject.pipe(
      untilDestroyed(this),
      auditTime(500),
      switchMap(_ => from(waitOnConditionAsync(() => this.rendering === false, 30 * 1000))),
      switchMap(_ => from(this._render()))
    )
  }

  ngOnDestroy() { }

  ngAfterViewInit() {
    this._render$.subscribe()
    this.render()
  }

  public render() {
    this._renderRequestSubject.next()
  }

  private async _render() {
    try {
      const w = this.pdfContainer.nativeElement.clientWidth
      const desiredWidth = w
      const viewport = this.page.getViewport(1)
      const scale = desiredWidth / viewport.width
      const scaledViewport = this.page.getViewport(scale)

      // Prepare canvas using PDF page dimensions
      const canvas: HTMLCanvasElement = this.pdfCanvas.nativeElement
      const context = canvas.getContext('2d')
      canvas.height = scaledViewport.height
      canvas.width = scaledViewport.width
      canvas.style.height = `${scaledViewport.height}px`
      canvas.style.width = `${scaledViewport.width}px`

      // Render PDF page into canvas context
      const renderContext = {
        canvasContext: context,
        viewport: scaledViewport
      }

      const renderTask = await this.page.render(renderContext)

      // TODO: Allow canceling instead of only waiting
      // await renderTask.cancel()
      this.rendering = false
      if (this.responsive) {
        canvas.style.width = '100%'
        canvas.style.height = '100%'
      }
    } catch (err) {
      // PDF loading error
      console.error(err)
    }
  }

  onResized(event) {
    if (!this.responsive || this.renderUpdateThreshold === -1) { return }
    const containerRect = this.pdfContainer.nativeElement.getBoundingClientRect()
    const pdfWidth = this.pdfCanvas.nativeElement.width
    const pdfHeight = this.pdfCanvas.nativeElement.height
    const wDiff = Math.abs(containerRect.width - pdfWidth)
    const hDiff = Math.abs(containerRect.height - pdfHeight)
    if (wDiff > this.renderUpdateThreshold || hDiff > this.renderUpdateThreshold) {
      this.render()
    }

  }

}

import { coerceArray, coerceNumberProperty } from '@angular/cdk/coercion'
import { Component, Input, OnInit } from '@angular/core'
import { BehaviorSubject, from, Observable, ReplaySubject } from 'rxjs'
import { map, switchMap } from 'rxjs/operators'

const pdfjsLib = require('pdfjs-dist/build/pdf')

@Component({
  selector: 'seam-pdf-viewer',
  template: `
  <ng-container *ngFor="let page of pages$ | async">
    <seam-pdf-page
      class="mb-2"
      [page]="page | async"
      [responsive]="responsive"
      [shadow]="shadow"
      [renderUpdateThreshold]="renderUpdateThreshold">
    </seam-pdf-page>
  </ng-container>
  `,
  styles: [`:host { display: block; }`]
})
export class PdfViewerComponent implements OnInit {

  @Input()
  get pdfUrl(): string { return this._pdfUrl }
  set pdfUrl(value: string) {
    this._pdfUrl = value
    this._documentSubject.next(value)
  }
  private _pdfUrl: string

  @Input() shadow = false

  /**
   * Canvas will responsively scale and rerender if scaled more than
   * `renderUpdateThreshold` pixels.
   */
  @Input() responsive = false

  /**
   * The canvas will be rerendered if the canvas size changes by this many
   * pixels from the last render.
   *
   * To make the canvas responsive using only the initial size for its render
   * set the threshold to `-1`. You can still trigger a rerender by calling the
   * `render()` method.
   *
   * NOTE: Only used when `responsive` is `true`.
   */
  @Input() renderUpdateThreshold = 100

  /**
   * Range of pages to render.
   *
   * Example(page 1 to page 3):
   *  [pageRange]="[1,3]"
   */
  @Input()
  get pageRange(): number[] { return this._pageRange }
  set pageRange(value: number[]) {
    this._pageRange = value

    if (!this._pageRange) {
      this._pageNumbersSubject.next(undefined)
    }

    try {
      const range = coerceArray(this._pageRange)
      if (range.length !== 2) {
        throw new Error('[pdf-viewer] Invalid Page Range. Range array must have two numbers only.')
      }
      if (range[0] > range[1]) {
        throw new Error('[pdf-viewer] Invalid Page Range. Start of the range must be less than or equal to the end.')
      }

      const nums: number[] = []
      for (let i = range[0]; i <= range[1]; i++) {
        nums.push(i)
      }
      this._pageNumbersSubject.next(nums)
    } catch (err) {
      // TODO: Decide how to conveniently display these errors.
      console.error(err)
      this._pageNumbersSubject.next([])
    }
  }
  private _pageRange: number[]

  /**
   * Render a specific page.
   */
  @Input()
  get pageNumber(): number { return this._pageNumber }
  set pageNumber(value: number) {
    this._pageNumber = coerceNumberProperty(value, -1)
    this._pageNumbersSubject.next(this._pageNumber === -1 ? undefined : [ this._pageNumber ])
  }
  private _pageNumber = -1

  /**
   * Specific page numbers to render.
   */
  @Input()
  get pageNumbers(): number[] { return this._pageNumbers }
  set pageNumbers(value: number[]) {
    this._pageNumbers = value
    if (Array.isArray(this._pageNumbers)) {
      this._pageNumbersSubject.next(this._pageNumbers)
    } else {
      this._pageNumbersSubject.next(undefined)
    }
  }
  private _pageNumbers: number[]

  private _documentSubject = new ReplaySubject<any>(1)
  public document$: Observable<any>
  public pages$: Observable<any[]>

  /**
   * Undefined means all a pages
   */
  private _pageNumbersSubject = new BehaviorSubject<number[] | undefined>(undefined)

  constructor() {
    if (!pdfjsLib.GlobalWorkerOptions.workerSrc) {
      // tslint:disable-next-line:max-line-length
      pdfjsLib.GlobalWorkerOptions.workerSrc = `https://cdnjs.cloudflare.com/ajax/libs/pdf.js/${ (pdfjsLib as any).version }/pdf.worker.min.js`
    }
  }

  ngOnInit() {
    this.document$ = this._documentSubject.asObservable()
      .pipe(switchMap(url => from(pdfjsLib.getDocument(url).promise)))

    const pageNumbers$ = this._pageNumbersSubject.asObservable()

    this.pages$ = this.document$
      .pipe(
        switchMap(doc => pageNumbers$.pipe(
          map(pageNumbers => {
            const pages: any[] = []
            for (let i = 0; i < doc.numPages; i++) {
              if (!pageNumbers || pageNumbers.indexOf(i + 1) !== -1) {
                pages.push(from(doc.getPage(i + 1)))
              }
            }
            return pages
          })
        ))
      )
  }

}

