export class StoryLibConfirmDialogBasicServiceComponent {

  @Input() msgText: string

  constructor(
    private _confirmDialog: SeamConfirmDialogService
  ) { }

  open() {
    this._confirmDialog.open(this.msgText).afterClosed().subscribe(result => {
