  name$: Observable<string | undefined>

  constructor(
    private _route: ActivatedRoute,
    private _router: Router
  ) {
    console.log('name-ex', this)
    this.name$ = this._route.data.pipe(map(v => v['name'] || undefined))

    console.log('config', this._router.config)
