import { ITableCellData, ITableCellDataChange } from '../table/table-cell.models'
import { ITheSeamTableColumn, TheSeamTableCellType } from '../table/table-column'

import { ITableCellTypeManifest } from '../table-cell-types/table-cell-types-models'
import { TABLE_CELL_TYPE_MANIFEST } from '../table-cell-types/table-cell-types-tokens'

@Component({
  selector: 'seam-table-cell-type-selector',
  template: `
    <ng-template *ngIf="componentPortal; else noPortal" [cdkPortalOutlet]="componentPortal"></ng-template>
    <ng-template #noPortal>{{ value }}</ng-template>
  `,
  styles: [],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class TableCellTypeSelectorComponent<D = any, V = any> implements OnInit, AfterViewInit, OnChanges {

  @Input() type: TheSeamTableCellType
  @Input() value: V
  @Input() rowIndex: number
  @Input() row: D
  @Input() colData: ITheSeamTableColumn<D>

  public componentPortal: ComponentPortal<{}>

  private _data: ITableCellData | undefined
  private _dataChangeSubject: Subject<ITableCellDataChange>
  private _manifests: ITableCellTypeManifest[]

  constructor(
    private _injector: Injector,
    private _ref: ChangeDetectorRef,
    @Optional() @Inject(TABLE_CELL_TYPE_MANIFEST) manifests?: ITableCellTypeManifest[]
  ) { this._manifests = manifests || [] }

  ngOnInit() { }

  ngAfterViewInit() {
    const comp = this._getComponent(this.type)
    if (comp) {
      this._dataChangeSubject = new Subject<ITableCellDataChange>()

      this._data = {
        row: this.row,
        rowIndex: this.rowIndex,
        colData: this.colData,
        value: this.value,
        changed: this._dataChangeSubject.asObservable()
      }

      this.componentPortal = new ComponentPortal(comp, null, this._createInjector(this._data))
      this._ref.detectChanges()
    } else {
      if (isDevMode()) {
        throw new Error(`CellType '${this.type}' not found.`)
      } else {
        // TODO: Implement fallback
      }
    }
  }

  private _getComponent(name: string): ComponentType<{}> | undefined {
    const manifest = this._manifests.find(m => m.name === name)
    return manifest ? manifest.component : undefined
  }

  private _createInjector(cellData: ITableCellData): PortalInjector {
    const injectorTokens = new WeakMap()
    injectorTokens.set(TABLE_CELL_DATA, cellData)
    return new PortalInjector(this._injector, injectorTokens)
  }

  ngOnChanges(changes: SimpleChanges) {
    if (this._data) {
      const dataChanged = [
        this._tryUpdateDataProp(changes, 'row'),
        this._tryUpdateDataProp(changes, 'rowIndex'),
        this._tryUpdateDataProp(changes, 'colData'),
        this._tryUpdateDataProp(changes, 'value')
      ].findIndex(b => b === true) !== -1

      if (dataChanged && this._dataChangeSubject) {
        this._dataChangeSubject.next({
          data: this._data,
          changes
        })
      }
    }
  }

  private _tryUpdateDataProp(changes: SimpleChanges, prop: keyof Omit<ITableCellData, 'changed'>): boolean {
    if (this._data && changes.hasOwnProperty(prop)) {
      this._data[prop] = changes[prop].currentValue
      return true
    }
    return false
  }

}

import { coerceArray } from '@angular/cdk/coercion'
import { ChangeDetectionStrategy, Component, EventEmitter, Input, OnInit, Output } from '@angular/core'
import { DomSanitizer } from '@angular/platform-browser'

/**
 * An optional function passed into the `NgForOf` directive that defines how to track
 * changes for items in an iterable.
 * The function takes the iteration index and item ID.
 * When supplied, Angular tracks changes by the return value of the function.
 *
 * NOTE: Can't import the type from'@angular/core', because of a bug in angular-cli's
 * build that can't find exported types that only exist at compile time if there
 * are exports that exist at runtime in the same file.
 */
export type TrackByFunction<T> = (index: number, item: T) => any

export interface ITableColumn {
  prop: string
  name?: string
}

@Component({
  selector: 'seam-table',
  templateUrl: './table.component.html',
  styleUrls: ['./table.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class TableComponent<T = any> implements OnInit {

  @Input()
  get columns() { return this._columns }
  set columns(value: (string | ITableColumn)[]) {
    this._columns = value
    this._setColumns(value)
  }
  private _columns: (string | ITableColumn)[]

  @Input()
  get rows(): T[] { return this._rows }
  set rows(value: T[]) {
    this._rows = !!value ? coerceArray(value) : []

    if (this._rows.length < 1) {
      this._displayedRows = [
        { _emptyDisplay: true } as any
      ]
    } else {
      this._displayedRows = this._rows
    }
  }
  private _rows: T[] = []

  get displayedRows() { return this._displayedRows }
  private _displayedRows: T[] & { _colSpan?: number } = []

  @Input() trackBy: TrackByFunction<T>

  @Input() size: 'sm' | 'md' | undefined | null

  @Input() hasHeader = true

  public displayedRecords: ITableColumn[]
  public displayedColumns: string[]

  @Output() readonly actionRefreshRequest = new EventEmitter<void>()

  constructor(
    private _sanitizer: DomSanitizer
  ) { }

  ngOnInit() { }

  private _setColumns(cols: (string | ITableColumn)[]) {
    const newCols: ITableColumn[] = []

    for (const col of cols) {
      if (typeof col === 'string') {
        const newCol: ITableColumn = {
          prop: col,
          name: col
        }
        newCols.push(newCol)
      } else {
        let name = col.name
        if (name === undefined || name === null) {
          name = col.prop
        }
        const newCol: ITableColumn = {
          ...col,
          prop: col.prop,
          name
        }
        newCols.push(newCol)
      }
    }

    for (const col of newCols) {
      const _col = col as any
      if (_col && _col.cellTypeConfig && _col.cellTypeConfig.styles) {
        _col.cellTypeConfig.styles = this._sanitizer.bypassSecurityTrustStyle(_col.cellTypeConfig.styles)
      }
    }

    this.displayedRecords = newCols
    this.displayedColumns = newCols.map(c => c.prop)
  }

  public triggerActionRefreshRequest() {
    this.actionRefreshRequest.emit(undefined)
  }

}

