    &:hover,
    &.toggle-edit-editing {
      background-color: $light;

      .toggle-edit-edit-btn {
        opacity: 1;
        visibility: visible;
      }
    }
  }

  .toggle-edit-edit-btn {
    opacity: 0;
    visibility: hidden;
  }

  .toggle-edit-edit-btn,
  .toggle-edit-submit-btn,
  .toggle-edit-cancel-btn {
    transition: 250ms ease-in-out;
  }

